import React, {useContext, useState} from 'react'
import {Text, View, Button, StyleSheet, TextInput,Image,StatusBar, TouchableOpacity, ImageBackground, Platform} from 'react-native'
import Color from '../constants/colors';
import {Header} from '../components/Header'
import { Input } from 'react-native-elements';
import {RoundButton} from '../components/RoundButton'
import {Icon} from 'react-native-elements'
import { ScrollView } from 'react-native-gesture-handler';
import { GlobalContext } from '../context/GlobalContext';
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scroll-view'

function ViewEntry({navigation, route})
{
    const {itemId} = route.params;
    const id = itemId;
    const {plantEntriesContext} = useContext(GlobalContext) 
    const[plantEntries, setPlantEntries] = plantEntriesContext;
    let num = 0;
    const[sunlight, setSunlight] = useState([false,false,false]);
    const[water, setWater] = useState([false,false,false]);

    const index = findEntryIndex(id);  

    // Execute after plant entries update and delete has been pressed
    React.useEffect(() =>{

        let tempSunlight = [false,false,false];
        let tempWater = [false,false,false];

      for(let i = 0; i < plantEntries[index].sunlight; i++)
      {
            tempSunlight[i] = true;
      }

      for(let i = 0; i < plantEntries[index].water; i++)
      {
        tempWater[i] = true;
      }

      setSunlight([...tempSunlight]);
      setWater([...tempWater]);

    },[plantEntries])

    
    
    return(
        <KeyboardAwareScrollView contentContainerStyle = {{flexGrow:1}}>
            {/* Main Container */}
            <View style = {styles.mainContainer}>
                <StatusBar barstyle = "dark-content" hidden = {false} backgroundColor = {Color.theme} translucent = {true}/>

                    {/* Secondary Container Header - Blue */}
                    <View style = {styles.secondaryContainerHeader}>
                        <Image source = {require('../assets/header.png') } />

                        {/* Header Container - Red */}
                        <View style = {styles.headerContainer}>
                            {/* Tree Image */}
                            <Image source = {{uri:plantEntries[index].image}} style = {{resizeMode:'stretch', height:200, width:200, borderRadius:5}} />

                        </View>

                    </View>

                    {/* Content Container - Blue */}
                    <View style = {styles.contentContainer}>
                    
                        <View>
                            <View>
                                <View style = {{flexDirection:'row'}}>
                                    <Text style = {[styles.contentText, styles.ContentTextHeader]}>Plant's Nickname:</Text>
                                    <Text style = {styles.contentText}>{plantEntries[index].nickname}</Text>
                                </View>
                                <View style = {{flexDirection:'row'}}>
                                    <Text style = {[styles.contentText,styles.ContentTextHeader]}>Plant Species: </Text>
                                    <Text style = {styles.contentText}>{plantEntries[index].species}</Text>
                                </View>
                            </View>

                            <View style = {{marginTop:10}}>
                                <Text style = {[styles.contentText, styles.ContentTextHeader]}>Plant Classification:</Text>
                                <View style = {{flexDirection:'row'}}>
                                    {plantEntries[index].classification.map((name) => <RoundButton key = {name} name = {name}/>)}
                                </View>
                            </View>
                        </View>

                        {/* Weather Container */}
                        <View style = {{paddingTop:20, width:'90%'}}>

                            {/* Sunlight Set */}
