    function findEntryIndex(id)
    {
        for(let i = 0; i < plantEntries.length; i++)
        {
            if(plantEntries[i].key == id)
                return i;
        }

        return -1;
    }

    
    function formatDate(date)
    {
        let originalDate = date;

        let day;
        let month = -1;
        let year;

        let dateArray = originalDate.split("-");

        switch(dateArray[1].toLowerCase())
        {
            case "1":
                month = 'January';
                break;
            case "2":
                month = 'February';
                break;
            case "3":
                month = "March";
                break;
            case "4":
                month = "April";
                break;
            case "5":
                month = "May";
                break;
            case "6":
                month = "June";
                break;
            case "7":
                month = "July";
                break;
            case "8":
                month = "August";
                break;
            case "9":
                month = "September";
                break;
            case "10":
                month = "October";
                break;
            case "11":
                month = "November";
                break;
            case "12":
                month = "December";
                break;
        }

        day = dateArray[2];
        year = dateArray[0];
        console.log(dateArray[1]);
        console.log(month);

        let completeDate = `${month} ${day}, ${year}`;

        return completeDate;
    }

